<h3 id="分页查询" tabindex="-1"><a class="header-anchor" href="#分页查询" aria-hidden="true">#</a> 分页查询</h3>
<div class="language-php ext-php line-numbers-mode"><pre v-pre class="language-php"><code><span class="token keyword">public</span> <span class="token keyword">function</span> <span class="token function-definition function">testClient</span><span class="token punctuation">(</span><span class="token punctuation">)</span> <span class="token punctuation">{</span>
	<span class="token variable">$name</span> <span class="token operator">=</span> <span class="token variable">$_POST</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'name'</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
	<span class="token comment">//姓名</span>
	<span class="token variable">$tel</span> <span class="token operator">=</span> <span class="token variable">$_POST</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'tel'</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
	<span class="token comment">//手机号</span>
	<span class="token variable">$card_number</span> <span class="token operator">=</span> <span class="token variable">$_POST</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'card_number'</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
	<span class="token comment">//证件号</span>
	<span class="token comment">//构造查询条件</span>
	<span class="token variable">$matches</span><span class="token operator">=</span><span class="token punctuation">[</span><span class="token punctuation">]</span><span class="token punctuation">;</span>
	<span class="token keyword">if</span><span class="token punctuation">(</span><span class="token variable">$name</span><span class="token punctuation">)</span> <span class="token function">array_push</span><span class="token punctuation">(</span><span class="token variable">$matches</span><span class="token punctuation">,</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'match'</span><span class="token operator">=></span><span class="token punctuation">[</span><span class="token string single-quoted-string">'name'</span><span class="token operator">=></span><span class="token variable">$name</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
	<span class="token keyword">if</span><span class="token punctuation">(</span><span class="token variable">$tel</span><span class="token punctuation">)</span> <span class="token function">array_push</span><span class="token punctuation">(</span><span class="token variable">$matches</span><span class="token punctuation">,</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'match'</span><span class="token operator">=></span><span class="token punctuation">[</span><span class="token string single-quoted-string">'tel'</span><span class="token operator">=></span><span class="token variable">$tel</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
	<span class="token keyword">if</span><span class="token punctuation">(</span><span class="token variable">$card_number</span><span class="token punctuation">)</span> <span class="token function">array_push</span><span class="token punctuation">(</span><span class="token variable">$matches</span><span class="token punctuation">,</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'match'</span><span class="token operator">=></span><span class="token punctuation">[</span><span class="token string single-quoted-string">'card_number'</span><span class="token operator">=></span><span class="token variable">$card_number</span><span class="token punctuation">]</span><span class="token punctuation">]</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
	<span class="token variable">$page</span> <span class="token operator">=</span> <span class="token variable">$_POST</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'page'</span><span class="token punctuation">]</span><span class="token operator">?</span><span class="token variable">$_POST</span><span class="token punctuation">[</span><span class="token string single-quoted-string">'page'</span><span class="token punctuation">]</span><span class="token punctuation">:</span><span class="token number">1</span><span class="token punctuation">;</span>
	<span class="token comment">//页码</span>
	<span class="token variable">$size</span> <span class="token operator">=</span> <span class="token number">10</span><span class="token punctuation">;</span>
	<span class="token comment">//每页显示条数</span>
	<span class="token variable">$limit</span> <span class="token operator">=</span> <span class="token punctuation">(</span><span class="token variable">$page</span><span class="token operator">-</span><span class="token number">1</span><span class="token punctuation">)</span><span class="token operator">*</span><span class="token variable">$size</span><span class="token punctuation">;</span>
	<span class="token comment">//偏移量</span>
	<span class="token variable">$params</span><span class="token operator">=</span><span class="token punctuation">[</span>
	            <span class="token string single-quoted-string">'index'</span><span class="token operator">=></span><span class="token string single-quoted-string">'people_information'</span><span class="token punctuation">,</span>
	            <span class="token string single-quoted-string">'type'</span><span class="token operator">=></span><span class="token string single-quoted-string">'_doc'</span><span class="token punctuation">,</span>
	            <span class="token string single-quoted-string">'from'</span><span class="token operator">=></span><span class="token variable">$limit</span><span class="token punctuation">,</span> <span class="token comment">//偏移量</span>
	<span class="token string single-quoted-string">'size'</span><span class="token operator">=></span><span class="token variable">$size</span><span class="token punctuation">,</span> <span class="token comment">//每页显示条数</span>
	<span class="token string single-quoted-string">'body'</span><span class="token operator">=></span><span class="token punctuation">[</span>
	                    <span class="token string single-quoted-string">'query'</span><span class="token operator">=></span><span class="token punctuation">[</span>
	                        <span class="token string single-quoted-string">'bool'</span><span class="token operator">=></span><span class="token punctuation">[</span>
	                            <span class="token string double-quoted-string">"must"</span><span class="token operator">=></span><span class="token variable">$matches</span>
	                        <span class="token punctuation">]</span>
	                    <span class="token punctuation">]</span><span class="token punctuation">,</span>
	                    <span class="token string single-quoted-string">'_source'</span><span class="token operator">=></span><span class="token punctuation">[</span><span class="token string single-quoted-string">'name'</span><span class="token punctuation">,</span><span class="token string single-quoted-string">'birth'</span><span class="token punctuation">,</span><span class="token string single-quoted-string">'card_number'</span><span class="token punctuation">,</span><span class="token string single-quoted-string">'sex'</span><span class="token punctuation">,</span><span class="token string single-quoted-string">'address'</span><span class="token punctuation">,</span><span class="token string single-quoted-string">'tel'</span><span class="token punctuation">]</span><span class="token punctuation">,</span>
